	protected function upload_logos() {
		$images = array(
			'logo_gray'       => STM_LMS_PRO_PATH . '/assets/img/certificate-builder/logo.png',
			'logo_blue'       => STM_LMS_PRO_PATH . '/assets/img/certificate-builder/logo-blue.png',
